import React from "react";
import Form from "../components/Form";
import Results from "../components/Results";
import API from "../utils/API";

class Search extends React.Component {
    state = {
        value: "",
        Recipes: []
    };
    
    componentDidMount() {
        this.searchRecipe();
    }

    makeRecipe = RecipeData => {
        return {
                        
                recipeID: RecipeData.recipeID,
                title: RecipeData.title,
                image: RecipeData.image,
                sourceURL: RecipeData.spoonacularSourceUrl    
            
        }
    }

    searchRecipe = query => {
        API.getRecipe(query)
            .then(res => this.setState({ Recipes: res.data.results.map(RecipeData => this.makeRecipe(RecipeData)) }))
            
            .catch(err => console.error(err));
    };

    handleInputChange = event => {
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]: value
        });
        console.log("searcing for: ", value);
    };

    handleFormSubmit = event => {
        console.log("inside form submit");
        event.preventDefault();
        this.searchRecipe(this.state.search);
    };

    render() {
        return (
            <div>
                <Form
                    search={this.state.search}
                    handleInputChange={this.handleInputChange}
                    handleFormSubmit={this.handleFormSubmit}
                />
                <div className="container">
                    <h2></h2>
                    {/* <Results Recipes={this.state.Recipes} /> */}
                </div>
            </div>
        )
    }
}

export default Search;
